import { useState, useEffect } from "react";

const CountDown = (props) => {
    const [duration, setDuration] = useState(10); 
    useEffect(()=>{
        const timer = setInterval(()=>{
            setDuration(duration - 1)
        }, 1000)
    },[])
    return (
        <div className="countdown-container">
            {duration}
        </div>
    )
}
export default CountDown;
