import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { deleteUser } from '../../../Services/apiService';
import { toast } from "react-toastify";
import { Toast } from 'bootstrap';
const ModalDeleteUser = (props) => {
    const { show, setShow, dataDelete } = props;
    const handleClose = (id) => setShow(false);
    const handleConfirmDele = async () => {
        let res = await deleteUser(dataDelete.id);
        console.log('component response: ', res);
        if (res && res.EC === 0) {
            toast.success(res.EM);
            handleClose();
            await props.fetchListUser();
        }
        if (res && res.EC !== 0) {
            toast.error(res.EM);
        }
    }
    return (
        <>
            <Modal show={show} onHide={handleClose} backdrop="static">
                <Modal.Header closeButton>
                    <Modal.Title>Confirm delete a user ?</Modal.Title>
                </Modal.Header>
                <Modal.Body>Are you sure to delete this user, email: <b>{dataDelete && dataDelete.email ? dataDelete.email : ""}</b> ?</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Cancel
                    </Button>
                    <Button variant="primary" onClick={() => handleConfirmDele()} >
                        Confirm
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default ModalDeleteUser
