import { useState } from "react"
import Select from "react-select"
import './Question.scss'
import { TbHeartPlus } from "react-icons/tb"
import { BiCheckboxMinus } from "react-icons/bi"
import { IoIosAdd } from "react-icons/io"
import { AiFillMinusCircle } from "react-icons/ai"
import { RiImageAddFill } from "react-icons/ri"
import { v4 as uuidv4 } from 'uuid'
import { cloneDeep } from "lodash"
import _ from "lodash"

const Questions = (props) => {
    const options = [
        { value: 'EASY', label: 'EASY' },
        { value: 'MEDIUM', label: 'MEDIUM' },
        { value: 'HARD', label: 'HARD' },
    ]
    const [selectedQuiz, setSelectedQuiz] = useState({});
    const [questions, setQuestions] = useState(
        [
            {
                id: uuidv4(),
                description: '',
                image: '',
                imageName: '',
                answers: [
                    {
                        id: uuidv4(),
                        description: 'answer 1',
                        isCorrect: false
                    },
                ]
            },

        ])
    const handleAddRemoveQuestions = (type, id) => {
        if (type === 'ADD') {
            const newQuestion = {
                id: uuidv4(),
                description: '',
                image: '',
                imageName: '',
                answers: [
                    {
                        id: uuidv4(),
                        description: '',
                        isCorrect: false
                    },

                ]
            }
            setQuestions([...questions, newQuestion]);
        }

        if (type === 'REMOVE') {
            let questionClone = _.cloneDeep(questions);
            questionClone = questionClone.filter(item => item.id !== id)
            setQuestions(questionClone);
        }
    }
    const handleAddRemoveAnswer = (type, questionId, id) => {
        let questionsClone = _.cloneDeep(questions);
        if (type === 'ADD') {
            const newAnswer =
            {
                id: uuidv4(),
                description: '',
                isCorrect: false
            }
            let index = questionsClone.findIndex(item => item.id === questionId);
            questionsClone[index].answers.push(newAnswer);
            setQuestions(questionsClone);
        }

        if (type === 'REMOVE') {
            let index = questionsClone.findIndex(item => item.id === questionId);
            questionsClone[index].answers = questionsClone[index].answers.filter(item => item.id !== id)
            setQuestions(questionsClone);
        }
    }
    const handleOnChange = (type, questionId, value) => {
        if (type === 'QUESTION') {
            let questionClone = _.cloneDeep(questions);
            let index = questionClone.findIndex(item => item.id === questionId)
            if (index > -1) {
                questionClone[index].description = value;
                setQuestions(questionClone);
            }
        }
    }
    return (
        <div className="question-container">
            <div className="title">
                Mange Questions
            </div>
            <hr />
            <div className="add-new-question">
                <div className="col-6 form-group">
                    <label className="mb-2">Select Quiz:</label>
                    <Select
                        value={selectedQuiz}
                        defaultValue={selectedQuiz}
                        onChange={setSelectedQuiz}
                        options={options}
                    />
                </div>
                <div className="mt-3 mb-2">Add questions:</div>
                {
                    questions && questions.length > 0 && questions.map((question, index) => {
                        return (
                            <div key = {question.id } className="q-main mb-5">
                                <div className="question-content">
                                    <div className="form-floating description">
                                        <input type="text" className="form-control"
                                            placeholder="name@example.com"
                                            value={question.description}
                                            onChange={(event) => handleOnChange('QUESTION', question.id, event.target.value)} />
                                        <label>Question {index + 1} description</label>
                                    </div>
                                    <div className="group-upload">
                                        <label className="label-upload"><RiImageAddFill /></label>
                                        <input type="file" hidden
                                        onChange={(event) => handleOnChangeFileQuestion(question.id, Event)}></input>
                                        <span>No file uploaded</span>
                                    </div>
                                    <div className="btn-add">

                                        <span onClick={() => handleAddRemoveQuestions('ADD', '')}>
                                            <IoIosAdd className="icon-add" /></span>
                                        {questions.length > 1 &&
                                            <span onClick={() => handleAddRemoveQuestions('REMOVE', question.id)}>
                                                <AiFillMinusCircle className="icon-remove" /></span>
                                        }

                                    </div>
                                </div>
                                {
                                    question.answers && question.answers.length > 0
                                    && question.answers.map((answer, index) => {
                                        return (
                                            <div className="answers-content" key={answer.id}>
                                                <input className="form-check-input iscorrected" type="checkbox" />
                                                <div className="form-floating col-6 description">
                                                    <input type="type" className="form-control answer-name"
                                                        placeholder="answer 1" value={answer.description} />
                                                    <label>Answer {index + 1}</label>
                                                </div>
                                                <div className="btn-group">
                                                    <span onClick={() => handleAddRemoveAnswer('ADD', question.id)}>
                                                        <IoIosAdd className="icon-add" />
                                                    </span>
                                                    {question.answers.length > 1 &&
                                                        <span
                                                            onClick={() => handleAddRemoveAnswer('REMOVE', question.id, answer.id)}>
                                                            <BiCheckboxMinus className="icon-remove" />
                                                        </span>
                                                    }

                                                </div>

                                            </div>
                                        )
                                    })
                                }
                            </div>
                        )
                    })
                }




            </div>
        </div>
    )
}
export default Questions
