import _ from "lodash";
import { useState } from "react";
import Lightbox from "react-awesome-lightbox";
const Question = (props) => {
    const { data, index } = props;
    const [isPreviewImage, setIsPreviewImage] = useState(false);
    if (_.isEmpty(data)) {
        return (<></>)
    }
    
    const handleCheckedbox =(Event,aId, qId)=>{
        props.handleCheckBox(aId, qId);
    }
    return (
        <>
            {
            data.image ? 
            <div className="q-image">
                <img src={`data: image/jpeg;base64,${data.image}`} onClick={() => setIsPreviewImage(true)} style={{cursor: p}}/>
                       {isPreviewImage === true &&
                            <Lightbox image={`data: image/jpeg;base64,${data.image}`}
                                title={"Question Image"}
                                onClose={() => setIsPreviewImage(false)}>
                            </Lightbox>}
            </div>
                : <div className="q-image"></div>
            }
            <div className="question">Question {index + 1}: {data.questionDescription} ?</div>
            <div className="answer">
                {data.answers && data.answers.length > 0 &&
                    data.answers.map((a, index) => {
                        return (
                            <div key={`answer-${index}`}
                                className="A-child">
                                <div className="form-check">
                                    <input className="form-check-input" type="checkbox" 
                                    checked = {a.isSelected}
                                        onChange={(Event) => handleCheckedbox(Event, a.id, data.questionId)}/>
                                    <label className="form-check-label" >
                                        {a.description}
                                    </label>
                                </div>
                            </div>
                        )
                    })}
            </div>
        </>
    )
}

export default Question;
