import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { deleteQuiz } from '../../../../Services/apiService';
import { toast } from "react-toastify";

const ModalDeleteQuiz = (props) => {
    const { show, setShow, dataDeleteQuiz } = props;
    const handleClose = () => setShow(false);
    const handleConfirmDele = async () => {
        let res = await deleteQuiz(dataDeleteQuiz.id);
        if (res && res.EC === 0) {
            toast.success(res.EM);
            handleClose();
            await props.fetchQuiz()
        }
        if (res && res.EC !== 0) {
            toast.error(res.EM);
        }
    }
    return (
        <>
            <Modal show={show} onHide={handleClose} backdrop="static">
                <Modal.Header closeButton>
                    <Modal.Title>Confirm delete a quiz ?</Modal.Title>
                </Modal.Header>
                <Modal.Body>Are you sure to delete this quiz ?</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Cancel
                    </Button>
                    <Button variant="primary" onClick={() => handleConfirmDele()} >
                        Confirm
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default ModalDeleteQuiz
