import { postRegister } from "../../Services/apiService";
import { toast } from "react-toastify";
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import './Register.scss'
import { IoEyeOff, IoEyeSharp } from "react-icons/io5";
const Register = (props) => {
    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isShowPassword, setIsShowPassword] = useState(false);
    const validateEmail = (email) => {
        return String(email)
            .toLowerCase()
            .match(
                /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|.(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
            );
    };
    const handleRegister = async () => {
        //validate
        const isValidEmail = validateEmail(email);
        if (!isValidEmail) {
            toast.error("Email is invalid! Please try again!")
            return;
        }
        //submit api
        let res = await postRegister(email, username,password);
        if (res && res.EC === 0) {
            toast.success(res.EM);
            navigate('/')
        }
        if (res && res.EC !== 0) {
            toast.error(res.EM);
        }
    }
    const handleFacebookSignUp = () => {
        navigate('/')
    }
    const navigate = useNavigate();
    return (
        <>
        <div className="register-container">
            <div className="header">     
                    <span>Already have an account ?</span>
                    <button>Log in</button>        
            </div>
                <div className='title col-4 mx-auto'>
                    LOGHORIZON
                </div>
                <div className='welcome col-4 mx-auto'>
                    Welcome to LOGHORIZON
                </div>
            <div className="facebook-signup">
                    <button className='btn-submit form-control'
                        onClick={() => handleFacebookSignUp()}>Sign up with Facebook</button>
            </div>
            <fieldset className="line">
                Or
            </fieldset>
            <div className="content-form col-4 mx-auto">
                    <div className='form-group'>
                        <label>Email</label>
                        <input type='email' className='form-control' value={email} onChange={(Event) => setEmail(Event.target.value)}></input>
                    </div>
                    <div className='form-group'>
                        <label>Username</label>
                        <input type='text' className='form-control' value={username} onChange={(Event) => setUsername(Event.target.value)}></input>
                    </div>
                    <div className='form-group'>
                        <label>Password</label>
                        <input type={isShowPassword ? "text":"password"} className='form-control'
                            value={password} onChange={(Event) => setPassword(Event.target.value)}>
                            </input>
                            {
                            !isShowPassword ? 
                            <span className="eyes-icons" onClick={() => set}><IoEyeOff /></span> : 
                            <span className="eyes-icons"><IoEyeSharp /></span>
                            }
                       
                    </div>
                    <div>
                        <button className='btn-submit'
                            onClick={() => handleRegister()}>Create a new account</button>
                    </div>
                    <div className='text-center' >
                        <span className='back' onClick={() => { navigate('/') }}> &#60;&#60; Go to HomePage</span>
                    </div>
            </div>

            
        </div>
        </>
    )
}

export default Register;
