import { Form } from "react-bootstrap";
import axios from "../utils/axiosCustomize";
const postcreateNewUser = (email, password, userName, role, image) => {
    const data = new FormData();
    data.append('email', email);
    data.append('password', password);
    data.append('username', userName);
    data.append('role', role);
    data.append('userImage', image);
    return axios.post('api/v1/participant', data);
}
const getDataQuiz = (quizId) => {
    return axios.get(`api/v1/questions-by-quiz?quizId=${quizId}`)
}
const postLogin = (email, password) => {
    return axios.post('api/v1/login', {email,password,delay:2000})
}
const postRegister = (email,username,password) => {
    return axios.post('api/v1/register', {email,username,password});
}
const getQuizByUser = () => {
    return axios.get('api/v1/quiz-by-participant')
}
const getAllUser = () => {
    return axios.get('api/v1/participant/all');
}
const putUpdateUser = (id,userName, role, image) => {
    const data = new FormData();
    data.append('id', id);
    data.append('username', userName);
    data.append('role', role);
    data.append('userImage', image);
    return axios.put('api/v1/participant', data);
}
const editQuiz = (id,name, difficulty, quizImage) => {
    const data = new FormData();
    data.append('name', name);
    data.append('difficult', difficulty);
    data.append('quizImage', quizImage);
    return axios.put('api/v1/quiz', data);
}
const deleteUser = (id) => {
    return axios.delete('api/v1/participant',{data:{id:id}})
}
const getUserWithPaginate = (page,limit) => {
    return axios.get(`api/v1/participant?page=${page}&limit=${limit}`)
}
const postSubmitQuiz = (data) =>{
    return axios.post('api/v1/quiz-submit',{...data})
}
const getAllQuizAdmin = () => {
    return axios.get('/api/v1/quiz/all')
}
const postCreateNewQuiz = (description, name, difficulty, quizImage) => {
    const data = new FormData();
    data.append('description', description);
    data.append('name', name);
    data.append('difficulty', difficulty);
    data.append('quizImage', quizImage);
    return axios.post('api/v1/quiz', data);
}
export { postcreateNewUser, getAllUser, putUpdateUser, deleteUser, getUserWithPaginate, postLogin, postRegister, getQuizByUser
    , getDataQuiz, postSubmitQuiz, postCreateNewQuiz, getAllQuizAdmin, editQuiz }
