import { useState, useRef } from 'react'
import './ManageQuiz.scss'
import Select from 'react-select'
import { postCreateNewQuiz } from '../../../../Services/apiService'
import { toast } from "react-toastify";
import  Accordion  from 'react-bootstrap/Accordion';
import TableQuiz from './TableQuiz';
import ModalEditQuiz from "./ModalEditQuiz";
import ModalViewUser from '../ModalViewUser';

const options = [
    {value: 'EASY', label:'EASY'},
    { value: 'MEDIUM', label: 'MEDIUM' },
    { value: 'HARD', label: 'HARD' },
]
const ManageQuiz = (props) => {
    
    const [name,setName] = useState('');
    const [description,setDescription] = useState('');
    const [type,setType] = useState('EASY');
    const [image,setImage] = useState(null);
    const [showModalEditTables, setShowModalEditTables] = useState(false);
    const [dataEdit, setDataEdit] = useState('');
    const fetchListUser = async () => {
        let res = await getAllUser();
        if (res.EC === 0) {
            setListUsers(res.DT);
        }
    }
    const childRef = useRef(null);
    const handleChangeFile = (Event) => {
        if (Event && Event.target && Event.target.files[0]) {
            setImage(Event.target.files[0])
    }
}
    const handleClickEditQuiz = (user) => {
        setShowModalEditTables(true);
        setDataEdit(user);
    }
    const resetData = () => {
        setDataEdit({})
    }
    const handleSubmitQuiz = async () => {
        let res = await postCreateNewQuiz(description, name, type?.value, image);
        if (res && res.EC === 0){
            toast.success(res.EM)
            setName('');
            setDescription('')
            setImage(null)
        }
        else{
            toast.error(res.EM)
        }
    }
    return (
        <div className="quiz-container">
            <Accordion defaultActiveKey="0">
                <Accordion.Item eventKey="0">
                    <Accordion.Header>
                        Manage Quizzes
                    </Accordion.Header>
                    <Accordion.Body>
                        <div className="add-new">
                            <fieldset className="border rounded-3 p-3">
                                <legend className="float-none w-auto px-3">Add new quiz:</legend>
                                <div className="form-floating mb-3">
                                    <input type="text" className="form-control" placeholder='your quiz name'
                                        value={name} onChange={(Event) => setName(Event.target.value)} />
                                    <label for="floatingInput">Name</label>
                                </div>
                                <div className="form-floating">
                                    <input type="text" className="form-control" placeholder='description'
                                        value={description} onChange={(Event) => setDescription(Event.target.description)} />
                                    <label for="floatingPassword">Description</label>
                                </div>
                                <div className='my-3'>
                                    <Select
                                        value={type}
                                        defaultValue={type}
                                        onChange={setType}
                                        options={options} />
                                </div>
                                <div className='more-actions form-group'>
                                    <label className='mb-1'>Upload Image</label>
                                    <input type='file' className='form-control'
                                        onChange={(Event) => handleChangeFile(Event)}></input>
                                </div>
                                <div className='mt-3'>
                                    <button className='btn btn-warning'
                                        onClick={() => handleSubmitQuiz()}>Save</button>
                                </div>
                            </fieldset>
                        </div>
                    </Accordion.Body>
                </Accordion.Item>
            </Accordion>
            <div className="list-detail">
                <TableQuiz 
                     handleClickEditQuiz={handleClickEditQuiz}
                />
            </div>
            <ModalEditQuiz
                show={showModalEditTables}
                setShow={setShowModalEditTables}
                dataEdit = {dataEdit}
                resetData={resetData}
                fetchQuiz={childRef.current.fetchQuiz}
            />
        </div>
    )
}

export default ManageQuiz
