import React, { useEffect, useState } from "react";
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Select from 'react-select'
import { editQuiz } from "../../../../Services/apiService";
import _ from 'lodash'
const ModalEditQuiz = (props) => {
    const options = [
        { value: 'EASY', label: 'EASY' },
        { value: 'MEDIUM', label: 'MEDIUM' },
        { value: 'HARD', label: 'HARD' },
    ]
    const {show, setShow, dataEdit} = props;
    const [name, setName] = useState(''); 
    const [difficulty, setDifficulty] = useState('');
    const [quizImage, setQuizImage] = useState('');
    const handleClose = () => {
        setShow(false);
        setName(''); setDifficulty(''); setQuizImage('')
        props.resetData();
    }
    const handleSaveEditQuiz = async () => {
        let res = await editQuiz(name,difficulty,quizImage)
    }
    useEffect(() => {
        if (!_.isEmpty(dataEdit)) {
            setName(dataEdit.name);
            setDifficulty(dataEdit.difficulty);
            if (dataEdit.quizImage) {
                setQuizImage(`data:image/jpeg;base64,${dataEdit.quizImage}`)
            }
        }
    }, [props.dataEdit])
    const hanleUploadImage = (Event) => {
        if (Event && Event.target && Event.target.files[0]) {
            setQuizImage(URL.createObjectURL(Event.target.files[0]));
            setImage(Event.target.files[0])
        }
    }
    return (
        <>
        <Modal show={show} onHide={handleClose} size='xl' backdrop="static"
            className='modal-add-quiz'>
            <Modal.Header closeButton>
                <Modal.Title>Edit Quiz</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                 <div className="col-md-6">
                    <label className="form-label" >Name:</label>
                    <input type="text" className="form-control" value={name} disabled
                        onChange={(Event) => setName(Event.target.value)} />
                    <div className='my-3'>
                        <Select
                            value={difficulty}
                            defaultValue={difficulty}
                            onChange={setDifficulty}
                            options={options} />
                    </div>
                        <div className='col-md-12'>
                            <label className="form-label label-upload" htmlFor='labelUpload'>
                                <HiOutlineViewGridAdd />Upload Image </label>
                            <input type='file' hidden id='labelUpload'
                                onChange={(Event) => hanleUploadImage(Event)}></input>
                        </div>
                        
                </div> 
            </Modal.Body>
            <Modal.Footer>
                    <Button onClick={() => handleClose()}>
                    Close
                </Button>
                <Button>
                    Save Changes
                </Button>
            </Modal.Footer>
        </Modal>
        </>
    )
}
export default ModalEditQuiz;
