import { useEffect} from "react";
import { useState } from "react";
import { getAllQuizAdmin } from "../../../../Services/apiService";
import ModalEditQuiz from "./ModalEditQuiz";
import ModalDeleteQuiz from "./ModalDeleteQuiz";

const TableQuiz = (props) => {
    const [listQuiz, setListQuiz] = useState([]);
    const [showModalEditTables, setShowModalEditTables] = useState(false);
    const [dataEdit, setDataEdit] = useState('');
    const [showModalDeleteTables, setShowModalDeleteTables] = useState(false);
    const [dataDeleteQuiz, setdataDeleteQuiz] = useState('');
    useEffect(()=>{
        fetchQuiz();
    },[])
    

    const handleClickEditQuiz = (user) => {
        setShowModalEditTables(true);
        setDataEdit(user);
    }
    const resetData = () => {
        setDataEdit({})
    }
    const fetchQuiz = async () => {
        let res = await getAllQuizAdmin();
        if (res && res.EC === 0){
            setListQuiz(res.DT)
        }
    }
    return (
        <>
        <div>List Quizzes: </div>
        <table className="table table-hover table-bordered mt-2 my-2">
            <thead>
                <th scope="col">ID</th>
                <th scope="col">Name</th>
                <th scope="col">Description</th>
                <th scope="col">Type</th>
                <th scope="col">Actions</th>
            </thead>
            <tbody>
                {listQuiz && listQuiz.map((item,index)=> {
                    return (
                        <tr key={`table-quiz-${index}`}>
                            <td>{item.id}</td>
                            <td>{item.name}</td>
                            <td>{item.description}</td>
                            <td>{item.difficulty}</td>
                            <td style={{display: "flex", gap: "15px" }}>
                                <button className="btn btn-warning"
                                    onClick={() => handleClickEditQuiz(item)}>Edit</button>
                                <button className="btn btn-danger">Delete</button>
                            </td>
                        </tr>
                    )
                })}
            </tbody>

        </table>
            <ModalEditQuiz
                show={showModalEditTables}
                setShow={setShowModalEditTables}
                dataEdit={dataEdit}
                resetData={resetData}
                fetchQuiz={fetchQuiz}
            />
            < ModalDeleteQuiz />
        </>
    )
}
export default TableQuiz;
