import { useEffect, forwardRef, useRef, useImperativeHandle } from "react";
import { useState } from "react";
import { getAllQuizAdmin } from "../../../../Services/apiService";
const TableQuiz = forwardRef((props,ref) => {
    const [listQuiz, setListQuiz] = useState([]);
    useImperativeHandle(ref,()=>({

    }))
    useEffect(()=>{
        fetchQuiz();
    },[])
    
    
    return (
        <>
        <div>List Quizzes: </div>
        <table className="table table-hover table-bordered mt-2 my-2">
            <thead>
                <th scope="col">ID</th>
                <th scope="col">Name</th>
                <th scope="col">Description</th>
                <th scope="col">Type</th>
                <th scope="col">Actions</th>
            </thead>
            <tbody>
                {listQuiz && listQuiz.map((item,index)=> {
                    return (
                        <tr key={`table-quiz-${index}`}>
                            <td>{item.id}</td>
                            <td>{item.name}</td>
                            <td>{item.description}</td>
                            <td>{item.difficulty}</td>
                            <td style={{display: "flex", gap: "15px" }}>
                                <button className="btn btn-warning"
                                    onClick={() => props.handleClickEditQuiz(item)}>Edit</button>
                                <button className="btn btn-danger">Delete</button>
                            </td>
                        </tr>
                    )
                })}
            </tbody>

        </table>
        </>
    )
})
export default TableQuiz;
