import React, { useEffect, useState } from "react";
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Select from 'react-select'
import { editQuiz } from "../../../../Services/apiService";
import { HiOutlineViewGridAdd } from 'react-icons/hi';
import _ from 'lodash'
import { toast } from "react-toastify";

const ModalEditQuiz = (props) => {
    const { show, setShow, dataEdit } = props;
    const [name, setName] = useState(''); 
    const [description,setDescription] = useState('');
    const [difficulty, setDifficulty] = useState('');
    const [quizImage, setQuizImage] = useState('');
    const [previewImg, setPreviewImg] = useState('')
    const handleClose = () => {
        setShow(false);
        setName(''); setDifficulty(''); setQuizImage(''); setPreviewImg(""); setDescription("")
        props.resetData();
    }
    const handleSaveEditQuiz = async () => {
        let res = await editQuiz(dataEdit.id, description, name, difficulty, previewImg);
        if (res && res.EC === 0) {
            toast.success(res.EM);
            handleClose();
            await props.fetchQuiz();
        }
        if (res && res.EC !== 0) {
            toast.error(res.EM);
        }
    }
    useEffect(() => {
        if (!_.isEmpty(dataEdit)) {
            setName(dataEdit.name);
            setDifficulty(dataEdit.difficulty);
            setDescription(dataEdit.description);
            if (dataEdit.image) {
                setPreviewImg(`data:image/jpeg;base64,${dataEdit.image}`)
            }
        }
    }, [props.dataEdit])
    const hanleUploadImage = (Event) => {
        if (Event && Event.target && Event.target.files[0]) {
            setPreviewImg(URL.createObjectURL(Event.target.files[0]));
            setQuizImage(Event.target.files[0])
        }
    }
    return (
        <>
        <Modal show={show} onHide={handleClose} size='xl' backdrop="static"
            className='modal-add-quiz'>
            <Modal.Header closeButton>
                <Modal.Title>Edit Quiz</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <form className="row g-3">
                 <div className="col-md-6">
                    <label className="form-label" >Name:</label>
                    <input type="text" className="form-control" value={name}
                        onChange={(Event) => setName(Event.target.value)} />
                 </div>
                    <div className="col-md-6">
                    <label className="form-label" >Description:</label>
                    <input type="text" className="form-control" value={description}
                            onChange={(Event) => setDescription(Event.target.value)} />
                    </div>
                        <div className='col-md-4'>
                            <select className="form-select" value={difficulty} 
                            onChange={(Event) => setDifficulty(Event.target.value)}>
                                <option selected value="EASY">EASY</option>
                                <option value="MEDIUM">MEDIUM</option>
                                <option value="HARD">HARD</option>
                            </select>
                    </div>
                        <div className='col-md-12'>
                            <label className="form-label label-upload" htmlFor='labelUpload'>
                                <HiOutlineViewGridAdd />Upload Image </label>
                            <input type='file' hidden id='labelUpload'
                                onChange={(Event) => hanleUploadImage(Event)}></input>
                        </div>
                        <div className='col-md-12 img-preview'>
                            {previewImg ? <img src={previewImg} />
                                : <span>Preview Image</span>}
                        </div>
                    </form>
            </Modal.Body>
            <Modal.Footer>
                    <Button onClick={() => handleClose()}>
                    Close
                </Button>
                    <Button onClick={() => handleSaveEditQuiz()}>
                    Save Changes
                </Button>
            </Modal.Footer>
        </Modal>
        </>
    )
}
export default ModalEditQuiz;
