import React, { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { HiOutlineViewGridAdd } from 'react-icons/hi';
import { toast } from "react-toastify";
import { Toast } from 'bootstrap';
import _ from 'lodash'
import { putUpdateUser } from '../../../Services/apiService'
const ModalUpdateUser = (props) => {
    const { show, setShow, dataUpdate, resetData } = props
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [userName, setUsername] = useState('');
    const [image, setImage] = useState('')
    const [role, setRole] = useState('USER');
    
    
    const handleClose = () => {
        setShow(false);
        setEmail(""); setPassword(""); setUsername(""); setImage(""); setRole("USER"); setPreviewImg("");
        props.res
    }
    useEffect(() => {
        if (!_.isEmpty(dataUpdate)) {
            setEmail(dataUpdate.email);
            setUsername(dataUpdate.username);
            setRole(dataUpdate.role);
            if (dataUpdate.image){
               setPreviewImg(`data:image/jpeg;base64,${dataUpdate.image}`)}
        }
    }, [props.dataUpdate])
    const handleShow = () => setShow(true);
    const [previewImg, setPreviewImg] = useState('')

    const hanleUploadImage = (Event) => {
        if (Event && Event.target && Event.target.files[0]) {
            setPreviewImg(URL.createObjectURL(Event.target.files[0]));
            setImage(Event.target.files[0])
        }
        else {

        }
    }
    
    const handleSubmitCreateUser = async () => {
        let res = await putUpdateUser(dataUpdate.id,userName, role, image);
        console.log('component response: ', res);
        if (res && res.EC === 0) {
            toast.success(res.EM);
            handleClose();
            await props.fetchListUser();
        }
        if (res && res.EC !== 0) {
            toast.error(res.EM);
        }


    }
    return (
        <>
            {/* <Button variant="primary" onClick={handleShow}>
                Launch demo modal
            </Button> */}

            <Modal show={show} onHide={handleClose} size='xl' backdrop="static"
                className='modal-add-user'>
                <Modal.Header closeButton>
                    <Modal.Title>Update a user</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form className="row g-3">
                        <div className="col-md-6">
                            <label className="form-label" >Email</label>
                            <input type="email" className="form-control" value={email} disabled
                                onChange={(Event) => setEmail(Event.target.value)} />
                        </div>
                        <div className="col-md-6">
                            <label className="form-label" >Password</label>
                            <input type="password" className="form-control" value={password} disabled
                                onChange={(Event) => setPassword(Event.target.value)} />
                        </div>
                        <div className="col-md-6">
                            <label className="form-label" >Username</label>
                            <input value={userName}
                                onChange={(Event) => setUsername(Event.target.value)}
                                type="text" className="form-control" placeholder="Apartment, studio, or floor" />
                        </div>
                        <div className="col-md-4">
                            <label className="form-label">Role</label>
                            <select className="form-select" value={role} onChange={(Event) => setRole(Event.target.value)}>
                                <option selected value="USER">USER</option>
                                <option value="ADMIN">ADMIN</option>
                            </select>
                        </div>
                        <div className='col-md-12'>
                            <label className="form-label label-upload" htmlFor='labelUpload'>
                                <HiOutlineViewGridAdd />Upload Image </label>
                            <input type='file' hidden id='labelUpload'
                                onChange={(Event) => hanleUploadImage(Event)}></input>
                        </div>
                        <div className='col-md-12 img-preview'>
                            {previewImg ? <img src={previewImg} />
                                : <span>Preview Image</span>}


                        </div>
                    </form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={() => handleSubmitCreateUser()}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default ModalUpdateUser
