import './DashBoard.scss'
import { Tooltip,Legend,Bar, CartesianGrid, XAxis, YAxis, BarChart,ResponsiveContainer } from 'recharts';
import { getOverView } from '../../../Services/apiService';
import { useEffect, useState } from 'react';
const Dashboard = (props) => {
    const [dataOverView, setDataOverView] = useState([]);
    const [dataChart, setDataChart] = useState([]);
    useEffect(()=>{
        fetchDataOverView();
    },[])
    const fetchDataOverView =async () => {
        let res = await getOverView();
        if (res && res.EC == 0){
            setDataOverView(res.DT);
            // process chart data
            let Qz = 0, Qs = 0, Ans = 0;
            Qz = res.DT.others.countQuiz;
            
            const data = [
                {
                    "name": "Quizzes",
                    "Qz": 4000,

                },
                {
                    "name": "Questions",
                    "Qs": 3000,

                },
                {
                    "name": "Answers",
                    "Ans": 2000,

                }
            ]
        }
    }
    
    return (
        <div className="dashboard-container">
           <div className='title'>
                DashBoard
           </div>
           <div className='content'>
                <div className='c_left'>
                    <div className='child'>
                        <div className='text-1'>Total Users</div>
                        <span className='text-2'>{dataOverView && dataOverView.users 
                        && dataOverView.users.total ? 
                        <>{dataOverView.users.total}</> :<>0</>}</span>
                    </div>
                    <div className='child'>
                        <div className='text-1'>Total Quizzes</div>
                        <span className='text-2'>
                            {dataOverView && dataOverView.users
                                && dataOverView.others.countQuiz ?
                                <>{dataOverView.others.countQuiz}</> : <>0</>}
                        </span>
                    </div>
                    <div className='child'>
                        <div className='text-1'>Total Questions</div>
                        <span className='text-2'>
                            {dataOverView && dataOverView.users
                                && dataOverView.others.countQuestions ?
                                <>{dataOverView.others.countQuestions}</> : <>0</>}
                        </span>
                    </div>
                    <div className='child'>
                        <div className='text-1'>Total Answers</div>
                        <span className='text-2'>
                            {dataOverView && dataOverView.users
                                && dataOverView.others.countAnswers ?
                                <>{dataOverView.others.countAnswers}</> : <>0</>}
                        </span>
                    </div>
                </div>
                <div className='c_right'>
                    < ResponsiveContainer width="95%" height={"100%"}>
                        <BarChart data={data}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="name" />
                            <YAxis />
                            <Tooltip />
                            <Legend />
                            <Bar dataKey="Qz" fill="#8884d8" />
                            <Bar dataKey="Qs" fill="#82ca9d" />
                            <Bar dataKey="Ans" fill="#ccc" />
                        </BarChart>
                    </ResponsiveContainer>
                </div>
           </div>
        </div>
    )
}
export default Dashboard
