import { useState } from 'react';
import './Login.scss'
import { toast } from "react-toastify";
import { useNavigate } from 'react-router-dom';
import { postLogin } from '../../Services/apiService';
import {useDispatch} from 'react-redux'
import { FaSpinner  } from 'react-icons/fa'
const Login = (props) => {
    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');
    const [isLoading,setIsLoading] = useState(false)
    const dispatch = useDispatch();
    const validateEmail = (email) => {
        return String(email)
            .toLowerCase()
            .match(
                /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|.(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
            );
    };
    const handleLogin = async() => {
        //validate
        const isValidEmail = validateEmail(email);
        if (!isValidEmail) {
            toast.error("Email is invalid! Please try again!")
            return;
        }
        //submit api
        setIsLoading(true)
        let res = await postLogin(email,password);
        if (res && res.EC === 0) {
            dispatch({
                type: 'FETCH_USER_LOGIN_SUCCESS',
                payload: res
            })
            toast.success(res.EM);
            navigate('/')
        }
        if (res && res.EC !== 0) {
            toast.error(res.EM);
        }
    }
    const navigate = useNavigate();
    return (
        <div className="login-container">
            <div className='header'>
                <span>Don't have an account yet ?</span> 
                <button>Sign up</button>
            </div>
            <div className='title col-4 mx-auto'>
                LOGHORIZON
            </div>
            <div className='welcome col-4 mx-auto'>
                Hello, who's this ?
            </div>
            <div className='content-form col-4 mx-auto'>
                <div className='form-group'>
                    <label>Email</label>
                    <input type='email' className='form-control' value={email} onChange={(Event) => setEmail(Event.target.value)}></input>
                </div>
                <div className='form-group'>
                    <label>Password</label>
                    <input type="password" className='form-control'
                        value={password} onChange={(Event) => setPassword(Event.target.value)}></input>
                </div>
                <span className='forgot-password '>Forgot password ? </span>
                <div>
                    <button className='btn-submit'
                        onClick={() => handleLogin()} disabled={isLoading}>
                        <FaSpinner className='loaderIcon'/>
                        <span>Login to LOGHORIZON</span></button>
                </div>
                <div className='text-center' >
                    <span className='back' onClick={() => { navigate('/') }}> &#60;&#60; Go to HomePage</span>
                </div>
              
            </div>
        </div>
    )
}

export default Login;
