import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { HiOutlineViewGridAdd } from 'react-icons/hi';
const ModalCreateUser = () => {
    const [show, setShow] = useState(false);
    const [email,setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [userName, setUsername] = useState('');
    const [image, setImage] = useState('')
    const [role,setRole] = useState('USER');
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    const [previewImg, setPreviewImg] = useState('')
    
    const hanleUploadImage = (Event) => {
        setPreviewImg(URL.createObjectURL)
    }
    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Launch demo modal
            </Button>

            <Modal show={show} onHide={handleClose} size='xl' backdrop="static"
            className='modal-add-user'>
                <Modal.Header closeButton>
                    <Modal.Title>Add new user</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form className="row g-3">
                        <div className="col-md-6">
                            <label className="form-label" value = {email} 
                            onChange={(Event) => setEmail(Event.target.value)}>Email</label>
                            <input type="email" className="form-control" />
                        </div>
                        <div className="col-md-6">
                            <label className="form-label" value = {password}
                                onChange={(Event) => setPassword(Event.target.value)}>Password</label>
                            <input type="password" className="form-control" />
                        </div>
                        <div className="col-md-6">
                            <label className="form-label" value={userName}
                                onChange={(Event) => setUsername(Event.target.value)}>Username</label>
                            <input type="text" className="form-control" placeholder="Apartment, studio, or floor" />
                        </div>
                        <div className="col-md-4">
                            <label className="form-label">Role</label>
                            <select className="form-select" onChange={(Event) => setRole(Event.target.value)}>
                                <option selected value="USER">USER</option>
                                <option value="ADMIN">ADMIN</option>
                            </select>
                        </div>
                        <div className='col-md-12'>
                            <label className="form-label label-upload" htmlFor='labelUpload'>
                                <HiOutlineViewGridAdd />Upload Image </label>
                            <input type='file' hidden id='labelUpload'
                            onChange={(Event) => hanleUploadImage(Event)}></input>
                        </div>
                        <div className='col-md-12 img-preview'>
                            {previewImg ? <img src="https://bit.ly/loghorizon-restaurant-backmenu" />
                                : <span>Preview Image</span> }
                           
                            
                        </div>
                    </form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default ModalCreateUser
