import ModalCreateUser from "./ModalCreateUser"
import './ManageUser.scss'
import {FcPlus} from 'react-icons/fc'
import TableUser from "./TableUser"
import { useEffect, useState } from "react"
import { getAllUser, getUserWithPaginate } from "../../../Services/apiService";
import ModalUpdateUser from "./ModalUpdateUser"
import ModalViewUser from "./ModalViewUser"
import ModalDeleteUser from "./ModalDeleteUser"
import TableUserPaginate from "./TableUserPaginate"
const ManageUser = (props) => {
    const LIMIT_USER = 6;
    const [showModalCreateuser, setshowModalCreateuser] = useState(false);
    const [showModalUpdateUser,setshowModalUpdateUser] = useState(false);
    const [showViewUser, setShowViewUser] = useState(false);
    const [showModalDelte,setShowModalDelete] = useState(false);
    const [dataUpdate,setdataUpdate] = useState({})
    const[dataView, setdataView] = useState({})
    const [dataDelete, setdataDelete] = useState({})
    const [listUsers, setListUsers] = useState([]);
    useEffect(() => {
        // fetchListUser();
        fetchListUserWithPaginate(1);
    }, []);
    const fetchListUser = async () => {
        let res = await getAllUser();
        if (res.EC === 0) {
            setListUsers(res.DT);
        }
    }
    const fetchListUserWithPaginate = async (page) => {
        let res = await getUserWithPaginate(page,LIMIT_USER);
        if (res.EC === 0) {
            setListUsers(res.DT.users);

        }
    }
    const handleClickUpdateUser = (user) => {
        setshowModalUpdateUser(true);
        setdataUpdate(user);
    }
    const handleViewUser = (user) => {
        setShowViewUser(true);
        setdataView(user);
    }
    const handleDeleteUser = (user) => {
         setShowModalDelete(true);
        setdataDelete(user)
    }
    const resetData = () => {
        setdataUpdate({})
    }
    return (
        <div className="manage-user-container">
            <div className="title">
                Manage User
            </div>
            <div className="uers-content">
                <div className="btn-add-new">
                    <button className="btn btn-primary" onClick={() => setshowModalCreateuser(true)}><FcPlus />Add new user</button>
                </div>
                <div className="table-users-container">
                    <TableUserPaginate listUsers={listUsers}
                    handleClickUpdateUser={handleClickUpdateUser}
                    handleViewUser={handleViewUser}
                    handleDeleteUser={handleDeleteUser}
                    fetchListUserWithPaginate={fetchListUserWithPaginate}/>
                </div>
                <ModalCreateUser show={showModalCreateuser} 
                setShow={setshowModalCreateuser}
                fetchListUser = {fetchListUser}
                />
                <ModalUpdateUser 
                    show={showModalUpdateUser}
                    setShow={setshowModalUpdateUser}
                    dataUpdate = {dataUpdate} 
                    fetchListUser={fetchListUser}
                    resetData={resetData}
                />
                <ModalViewUser 
                    show={showViewUser}
                    dataView={dataView}
                    setShow={setShowViewUser} 
                    resetData={resetData}/>
                <ModalDeleteUser 
                    show={showModalDelte}
                    setShow={setShowModalDelete}
                    dataDelete={dataDelete}
                    fetchListUser={fetchListUser}
                   />
            </div>
        </div>
    )
}
export default ManageUser
