import React, { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { HiOutlineViewGridAdd } from 'react-icons/hi';
import _ from 'lodash'
const ModalViewUser = (props) => {
    const { show, setShow, dataView, resetData } = props
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [userName, setUsername] = useState('');
    const [image, setImage] = useState('')
    const [role, setRole] = useState('USER');
    const [previewImg, setPreviewImg] = useState('')

    const handleClose = () => {
        setShow(false);
        setEmail(""); setPassword(""); setUsername(""); setImage(""); setRole("USER"); setPreviewImg("");
        props.resetData();
    }
    useEffect(() => {
        if (!_.isEmpty(dataView)) {
            setEmail(dataView.email);
            setUsername(dataView.username);
            setRole(dataView.role);
            if (dataView.image) {
                setPreviewImg(`data:image/jpeg;base64,${dataView.image}`)
            }
        }
    }, [props.dataView])
    

    return (
        <>
            <Modal show={show} onHide={handleClose} size='xl' backdrop="static"
                className='modal-add-user'>
                <Modal.Header closeButton>
                    <Modal.Title>View a user</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form className="row g-3">
                        <div className="col-md-6">
                            <label className="form-label" >Email</label>
                            <input type="email" className="form-control" value={email} disabled/>
                        </div>
                        <div className="col-md-6">
                            <label className="form-label" >Password</label>
                            <input type="password" className="form-control" value={password} disabled/>
                        </div>
                        <div className="col-md-6">
                            <label className="form-label" >Username</label>
                            <input value={userName} disabled
                                type="text" className="form-control" placeholder="Apartment, studio, or floor" />
                        </div>
                        <div className="col-md-4">
                            <label className="form-label">Role</label>
                            <select className="form-select" value={role}  disabled>
                                <option selected value="USER">USER</option>
                                <option value="ADMIN">ADMIN</option>
                            </select>
                        </div>
                        <div className='col-md-12'>
                            <label className="form-label label-upload" htmlFor='labelUpload'>
                                <HiOutlineViewGridAdd />Upload Image </label>
                            <input type='file' hidden id='labelUpload' disabled
                               ></input>
                        </div>
                        <div className='col-md-12 img-preview'>
                            {previewImg ? <img src={previewImg} />
                                : <span>Preview Image</span>}
                        </div>
                    </form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default ModalViewUser
