import { useEffect, useState } from "react"
import Select from "react-select"
import './QuizQA.scss'
import { BiCheckboxMinus } from "react-icons/bi"
import { IoIosAdd } from "react-icons/io"
import { AiFillMinusCircle } from "react-icons/ai"
import { RiImageAddFill } from "react-icons/ri"
import { v4 as uuidv4} from 'uuid'
import { getAllQuizAdmin, postNewQuestionForQuiz, postAnswerForQuestion, getQuizWithQa, postUpsertQA } from "../../../../Services/apiService";
import _ from "lodash"
import Lightbox from "react-awesome-lightbox";
import { toast } from "react-toastify"
const QuizQA = (props) => {
    const initSate = (
        [
        {
            id: uuidv4(),
            description: '',
            imageFile: '',
            imageName: '',
            answers: [
                {
                    id: uuidv4(),
                    description: '',
                    isCorrect: false
                },
            ]
        },
        ])
    const [selectedQuiz, setSelectedQuiz] = useState({});
    const [questions, setQuestions] = useState(initSate)
    const [prewImage, setPrewImage] = useState(false);
    const [listQuiz, setListQuiz] = useState([]);
    const fetchQuiz = async () => {
        let res = await getAllQuizAdmin();
        if (res && res.EC === 0) {
            let newQuiz = res.DT.map(item => {
                return {
                    value: item.id,
                    label: `${item.id}-${item.description}`
                }
            })
            setListQuiz(newQuiz);
        }
    }
    useEffect(()=>{
        fetchQuiz();
    }, [])
    const [dataPreviewImage, setDataPreviewImage] = useState({
        url: '',
        title: ''
    })
    const handleSubmitQuestion = async () => {
        //todo
        if (_.isEmpty(selectedQuiz)){
            toast.error("Please select quiz");
            return;
        }
        //validate question
        let isValidQ = true; let indexQ;
        for (let i = 0; i < questions.length; i++){
            if (!questions[i].description){
                isValidQ = false;
                indexQ = i;
                break;
            }
        }
        if (isValidQ === false) {
            toast.error(`Not empty description in question ${indexQ+1}`);
            return;
        }
        //validate answer
        let isValid = true; let indexAnswer; let indexQuestion;
        // for (let i = 0; i < questions.length; i++){
        //     let checkedAnswer = false;
        //     for (let j =0; j < questions[i].answers.length;j++){
        //         if (!questions[i].answers[j].description){
        //             isValid = false;
        //             indexAnswer = j;
        //             break;
        //         }
               
        //         if (questions[i].answers[j].isCorrect === true){
        //             checkedAnswer = true;
        //         }
        //     }
        //     indexQuestion = i;
        //     if (checkedAnswer === false) {
        //         toast.error("Please fill checked box");
        //         return;
        //     }
        //     if (isValid === false) break;
        // }
        let questionsClone = _.cloneDeep(questions);
        for (let i = 0; i < questionsClone.length; i++){
            if (questionsClone[i].imageFile){
                questions[i].imageFile =  toBase64(questionsClone[i].imageFile);
            }
        }
        let res = await postUpsertQA({
            quizId: selectedQuiz.value,
            questions: questionsClone
        });
        if (res && res.EC === 0){
            toast.success(res.EM);
        }
        const toBase64 = file => new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = () => resolve(reader.result);
            reader.onerror = reject;
        });
        if (isValid === false) {
            toast.error(`Please fill answer${indexAnswer + 1} in question${indexQuestion + 1}`);
            return;
        }
        toast.success("Create success !");
        setQuestions(initSate);
    }
    const handleAddRemoveQuestions = (type, id) => {
        if (type === 'ADD') {
            const newQuestion = {
                id: uuidv4(),
                description: '',
                imageFile: '',
                imageName: '',
                answers: [
                    {
                        id: uuidv4(),
                        description: '',
                        isCorrect: false
                    },

                ]
            }
            setQuestions([...questions, newQuestion]);
        }

        if (type === 'REMOVE') {
            let questionClone = _.cloneDeep(questions);
            questionClone = questionClone.filter(item => item.id !== id)
            setQuestions(questionClone);
        }
    }
    const handleAddRemoveAnswer = (type, questionId, id) => {
        let questionsClone = _.cloneDeep(questions);
        if (type === 'ADD') {
            const newAnswer =
            {
                id: uuidv4(),
                description: '',
                isCorrect: false
            }
            let index = questionsClone.findIndex(item => item.id === questionId);
            questionsClone[index].answers.push(newAnswer);
            setQuestions(questionsClone);
        }

        if (type === 'REMOVE') {
            let index = questionsClone.findIndex(item => item.id === questionId);
            questionsClone[index].answers = questionsClone[index].answers.filter(item => item.id !== id)
            setQuestions(questionsClone);
        }
    }
    const handleOnChange = (type, questionId, value) => {
        if (type === 'QUESTION') {
            let questionClone = _.cloneDeep(questions);
            let index = questionClone.findIndex(item => item.id === questionId)
            if (index > -1) {
                questionClone[index].description = value;
                setQuestions(questionClone);
            }
        }
    }
    const handleOnChangeFileQuestion = (questionId, Event) => {
        let questionClone = _.cloneDeep(questions);
        let index = questionClone.findIndex(item => item.id === questionId);
        if (index > - 1 && Event && Event.target && Event.target.files && Event.target.files[0]){
            questionClone[index].imageFile = Event.target.files[0];
            questionClone[index].imageName = Event.target.files[0].name;
            //.log('file image: ', questionClone[index]);
            setQuestions(questionClone);

        }
    }
    const handleOnChangeAnswer = (type,questionId, answerId, value) => {
        let questionClone = _.cloneDeep(questions);
        let index = questionClone.findIndex(item => item.id === questionId);
        if (index > - 1) {
            questionClone[index].answers = questionClone[index].answers.map((answer, index)=>{
                if (answer.id === answerId){
                    if (type==='CHECKED'){
                        answer.isCorrect = value;
                    }
                    if (type === 'INPUT'){
                        answer.description = value;
                    }
                }
                return answer;
            })
            setQuestions(questionClone);

        }
    }
    const handleOnClickImage = (questionId) => {
        let questionClone = _.cloneDeep(questions);
        let index = questionClone.findIndex(item => item.id === questionId);
        if (index > -1) {
           setDataPreviewImage({
               url: URL.createObjectURL(questionClone[index].imageFile),
               title: questionClone[index].imageName
           })
            setPrewImage(true);
        }
    }
    const urltoFile = async (url, filename, mimeType) => {
        if (url.startsWith('data:')) {
            var arr = url.split(','),
                mime = arr[0].match(/:(.*?);/)[1],
                bstr = atob(arr[arr.length - 1]),
                n = bstr.length,
                u8arr = new Uint8Array(n);
            while (n--) {
                u8arr[n] = bstr.charCodeAt(n);
            }
            var file = new File([u8arr], filename, { type: mime || mimeType });
            return Promise.resolve(file);
        }
        return fetch(url)
            .then(res => res.arrayBuffer())
            .then(buf => new File([buf], filename, { type: mimeType }));
    }
    const fetchQuizWithQA = async () => {
        let res = await getQuizWithQa(selectedQuiz.value);
        if (res && res.EC === 0){

            let newQA = [];
            for (let i = 0; i < res.DT.qa.length; i++){
                let q = res.DT.qa[i];
                if (q.imageFile){
                    q.imageName = `Question-${q.id}.png`
                    q.imageFile = await urltoFile(`data:image/png;base64,${q.imageFile}`,`Question-${q.id}`,'image/png')
                }
                newQA.push(q);
            }
            setQuestions(newQA);
           
        }
       
    }
    useEffect(() => {
        if (selectedQuiz && selectedQuiz.value){
            fetchQuizWithQA();
        }
       
    }, [selectedQuiz])
    return (
        <div className="question-container">
            <div className="add-new-question">
                <div className="col-6 form-group">
                    <label className="mb-2">Select Quiz:</label>
                    <Select
                        value={selectedQuiz}
                        defaultValue={selectedQuiz}
                        onChange={setSelectedQuiz}
                        options={listQuiz}
                    />
                </div>
                <div className="mt-3 mb-2">Add questions:</div>
                {
                    questions && questions.length > 0 && questions.map((question, index) => {
                        return (
                            <div key = {question.id } className="q-main mb-5">
                                <div className="question-content">
                                    <div className="form-floating description">
                                        <input type="text" className="form-control"
                                            placeholder="name@example.com"
                                            value={question.description}
                                            onChange={(event) => handleOnChange('QUESTION', question.id, event.target.value)} />
                                        <label>Question {index + 1} description</label>
                                    </div>
                                    <div className="group-upload">
                                        <label className="label-upload" htmlFor={`${question.id}`}><RiImageAddFill  /></label>
                                        <input type={'file'} hidden id = {`${question.id}`} 
                                        onChange={(Event) => handleOnChangeFileQuestion(question.id, Event)}></input>
                                        <span>{question.imageName ? 
                                        <span style={{cursor:'pointer'}} onClick={()=> handleOnClickImage(question.id)}>{question.imageName}</span> : 
                                        'No file uploaded'}</span>
                                    </div>
                                    <div className="btn-add">

                                        <span onClick={() => handleAddRemoveQuestions('ADD', '')}>
                                            <IoIosAdd className="icon-add" /></span>
                                        {questions.length > 1 &&
                                            <span onClick={() => handleAddRemoveQuestions('REMOVE', question.id)}>
                                                <AiFillMinusCircle className="icon-remove" /></span>
                                        }

                                    </div>
                                </div>
                                {
                                    question.answers && question.answers.length > 0
                                    && question.answers.map((answer, index) => {
                                        return (
                                            <div className="answers-content" key={answer.id}>
                                                <input className="form-check-input iscorrected" type="checkbox" 
                                                checked={answer.isCorrect}
                                                onChange={(Event) => handleOnChangeAnswer('CHECKED', question.id, answer.id,Event.target.checked)}/>
                                                <div className="form-floating col-6 description">
                                                    <input type="type" className="form-control answer-name" 
                                                        placeholder="answer 1" value={answer.description} 
                                                        onChange={(Event) => handleOnChangeAnswer('INPUT',question.id,answer.id,Event.target.value)}/>
                                                    <label>Answer {index + 1}</label>
                                                </div>
                                                <div className="btn-group">
                                                    <span onClick={() => handleAddRemoveAnswer('ADD', question.id)}>
                                                        <IoIosAdd className="icon-add" />
                                                    </span>
                                                    {question.answers.length > 1 &&
                                                        <span
                                                            onClick={() => handleAddRemoveAnswer('REMOVE', question.id, answer.id)}>
                                                            <BiCheckboxMinus className="icon-remove" />
                                                        </span>
                                                    }
                                                </div>
                                            </div>
                                        )
                                    })
                                }
                               
                            </div>
                        )
                    })
                }
                {
                    questions && questions.length > 0 && 
                    <button className="btn btn-primary"
                    onClick={()=>handleSubmitQuestion()}> Save questions</button>
                }
            </div>
            {prewImage === true &&
                <Lightbox image={dataPreviewImage.url}
                    title={dataPreviewImage.title}
                    onClose={() => setPrewImage(false)}>
                </Lightbox>
            }
        </div>
    )
}
export default QuizQA
