import ReactPaginate from 'react-paginate';
import React, { useEffect, useState } from 'react';

const TableUserPaginate = (props) => {
    const { listUsers, fetchListUserWithPaginate } = props;
    //const pageCount = Math.ceil(items.length / itemsPerPage);
    const handlePageClick = (event) => {
        props.fetchListUserWithPaginate(event.selected);
        console.log(
            `User requested page number ${event.selected}`
        );
    };
    return (
        <>
            <table className="table table-hover table-bordered">
                <thead>
                    <tr>
                        <th scope="col">ID</th>
                        <th scope="col">username</th>
                        <th scope="col">Email</th>
                        <th scope="col">Role</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {listUsers && listUsers.length > 0 &&
                        listUsers.map((item, index) => {
                            return (
                                <tr key={`table-users-${index}`}>
                                    <td>{item.id}</td>
                                    <td>{item.username}</td>
                                    <td>{item.email}</td>
                                    <td>@{item.role}</td>
                                    <td>
                                        <button className="btn btn-info"
                                            onClick={() => props.handleViewUser(item)}>View</button>
                                        <button className="btn btn-warning mx-3"
                                            onClick={() => props.handleClickUpdateUser(item)}>Update</button>
                                        <button className="btn btn-danger"
                                            onClick={() => props.handleDeleteUser(item)}>Delete</button>
                                    </td>

                                </tr>
                            )
                        })}
                    {listUsers && listUsers.length === 0 &&
                        <tr >
                            <td colSpan={'4'}>Not found data</td>
                        </tr>}


                </tbody>
            </table>
            <ReactPaginate
                nextLabel="next >"
                onPageChange={handlePageClick}
                pageRangeDisplayed={3}
                marginPagesDisplayed={2}
                pageCount={5}
                previousLabel="< previous"
                pageClassName="page-item"
                pageLinkClassName="page-link"
                previousClassName="page-item"
                previousLinkClassName="page-link"
                nextClassName="page-item"
                nextLinkClassName="page-link"
                breakLabel="..."
                breakClassName="page-item"
                breakLinkClassName="page-link"
                containerClassName="pagination"
                activeClassName="active"
                renderOnZeroPageCount={null}
            />
        </>
    )
}

export default TableUserPaginate
