import { useState } from "react"
import Select from "react-select"
import './Question.scss'
import { TbHeartPlus } from "react-icons/tb"
import { BiCheckboxMinus } from "react-icons/bi"
import { IoIosAdd } from "react-icons/io"
import { AiFillMinusCircle } from "react-icons/ai"
import { RiImageAddFill } from "react-icons/ri"
import { v4 as uuidv4 } from 'uuid'
import { cloneDeep } from "lodash"
import _ from "lodash"

const Questions = (props) => {
    const options = [
        { value: 'EASY', label: 'EASY' },
        { value: 'MEDIUM', label: 'MEDIUM' },
        { value: 'HARD', label: 'HARD' },
    ]
    const [selectedQuiz, setSelectedQuiz] = useState({});
    const [questions, setQuestions] = useState(
        [
            {
                id: uuidv4(),
                description: 'question 1',
                image: '',
                imageName: '',
                answers: [
                    {
                        id: uuidv4(),
                        description: 'answer 1',
                        isCorrect: false
                    },
                ]
            },

        ])
    const handleAddRemoveQuestions = (type, id) => {
        if (type === 'ADD') {
            const newQuestion = {
                id: uuidv4(),
                description: '',
                image: '',
                imageName: '',
                answers: [
                    {
                        id: uuidv4(),
                        description: '',
                        isCorrect: false
                    },

                ]
            }
            setQuestions([...questions, newQuestion]);
        }

        if (type === 'REMOVE') {
            let questionClone = _.cloneDeep(questions);
            questionClone = questionClone.filter(item => item.id !== id)
            setQuestions(questionClone);
        }
    }
    const handleAddRemoveAnswer = (type, id) => {
        let questionsClone = _.cloneDeep(questions);
        if (type === 'ADD') {
            const newAnswer = 
                    {
                        id: uuidv4(),
                        description: '',
                        isCorrect: false
                    }
        }
        let index  = questionsClone.findIndex(item => item)
        if (type === 'REMOVE') {
            let questionClone = questions;
            questionClone = questionClone.filter(item => item.id !== id)
            setQuestions(questionClone);
        }
    }
    return (
        <div className="question-container">
            <div className="title">
                Mange Questions
            </div>
            <hr />
            <div className="add-new-question">
                <div className="col-6 form-group">
                    <label className="mb-2">Select Quiz:</label>
                    <Select
                        value={selectedQuiz}
                        defaultValue={selectedQuiz}
                        onChange={setSelectedQuiz}
                        options={options}
                    />
                </div>
                <div className="mt-3 mb-2">Add questions:</div>
                {
                    questions && questions.length > 0 && questions.map((questions, index) => {
                        return (
                            <div className="q-main mb-5">
                                <div className="question-content">
                                    <div className="form-floating description">
                                        <input type="text" className="form-control" placeholder="name@example.com"
                                            value={questions.description} />
                                        <label>Question {index + 1} descriptiom</label>
                                    </div>
                                    <div className="group-upload">
                                        <label className="label-upload"><RiImageAddFill /></label>
                                        <input type="file" hidden></input>
                                        <span>No file uploaded</span>
                                    </div>
                                    <div className="btn-add">

                                        <span onClick={() => handleAddRemoveQuestions('ADD', '')}>
                                            <IoIosAdd className="icon-add" /></span>

                                        <span onClick={() => handleAddRemoveQuestions('REMOVE', questions.id)}>
                                            <AiFillMinusCircle className="icon-remove" /></span>
                                    </div>
                                </div>
                                {
                                    questions && questions.answers.length > 0
                                    && questions.answers.map((answers, index) => {
                                        return (
                                            <div className="answers-content" key={answers.id}>
                                                <input className="form-check-input iscorrected" type="checkbox" />
                                                <div className="form-floating col-6 description">
                                                    <input type="type" className="form-control answer-name"
                                                        placeholder="answer 1" value={answers.description} />
                                                    <label>Answer {index + 1}</label>
                                                </div>
                                                <div className="btn-group">
                                                    <span onClick={() => handleAddRemoveAnswer('ADD', questions.id)}><IoIosAdd className="icon-add" />
                                                    </span>
                                                    <span
                                                        onClick={() => handleAddRemoveAnswer('REMOVE', questions.id,answers.id)}><BiCheckboxMinus className="icon-remove" />
                                                    </span>
                                                </div>

                                            </div>
                                        )
                                    })
                                }
                            </div>
                        )
                    })
                }




            </div>
        </div>
    )
}
export default Questions
